import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {

  class Chat extends React.Component {
    constructor(props){
      super(props);
      this.handleChange = this.handleChange.bind(this);
      this.keyPress = this.keyPress.bind(this);
      this.state = {
        name : this.props.name,
        messages : ['kira', 'jagmuri'],
        currentmsg : ''
      }
    }

    keyPress(e){
      if(e.keyCode == 13)
       {
         var x = this.state.name + ": "+ this.state.currentmsg;
         this.setState({
           messages : [...this.state.messages,x]
         });
       }
    }

    handleChange(e) {
      this.setState({ currentmsg: e.target.value });
    }

    render(){
      var msgz = this.state.messages.map( (msg) => <li>{msg}</li>);
      return (
        <div id="chat">
          <ul id="messages">
            {msgz}
          </ul>
          <div id="typehold">
            <input type="text" onChange={this.handleChange} onKeyDown={this.keyPress} placeholder="enter a message..."></input>
          </div>
        </div>
      );
    }
  }
  return <Chat name="Alom"/>
}

export default App;
