import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  class Clock extends React.Component {
    constructor(props){
      super(props);
      this.state = {date: this.props.date};
    }

    componentDidMount(){
      this.timerID = setInterval(
        () => this.tick(), 2000
      );
    }

    componentWillUnmount(){
      clearInterval(this.timerID);
    }

    tick(){
      this.setState({
        date: new Date()
      });
    }
    render() {
      return (
        <div>
          <h1>Hello, world!</h1>
          <h2>It is {this.state.date.toLocaleTimeString()}.</h2>
        </div>
      );
    }
  }

  return (
    <div>
      <Clock date={new Date()} time={1000}/>
      <Clock date={new Date()} time={2000}/>
      <Clock date={new Date()} time={3000}/>
    </div>
  );
  // setInterval(tick, 1000);
}

export default App;
