            <button class='eightbit-btn eightbit-btn--proceed' onClick={this.setAvatar} >{'<< CHANGE AVATAR >>'}</button>

            <hr />

            <button class='eightbit-btn' onClick={this.playerFixed}>JOIN</button>
          </div>
        </div>
      </div>);
    }
  }

  return <PlayerSelection />;
