import React from 'react';
import logo, { ReactComponent } from './logo.svg';
import './App.css';
import ReactDOM from 'react-dom';

function App() {

  class Messages extends React.Component{
    constructor(props){
      super(props);
      this.state = {
        messages : this.props.messages
      }
    }
    render(){
      var msgz = this.state.messages.map( (msg) => <li>{msg}</li>);
      return (
          <ul id="messages">
            {msgz}
          </ul>
      );
    }
  }
  var servedMessages = [];
  function SendMessage(str){
      servedMessages.push(str);

  }

  class Chat extends React.Component {
    constructor(props){
      super(props);
      this.handleChange = this.handleChange.bind(this);
      this.keyPress = this.keyPress.bind(this);
      this.state = {
        name : this.props.name,
        currentmsg : ''
      }
    }

    keyPress(e){
      if(e.keyCode == 13)
       {
        //  var x = this.state.name + ": "+ this.state.currentmsg;
        //  this.setState({
        //    messages : [...this.state.messages,x],
        //  });
        //  e.target.value = '';
        SendMessage(this.state.name + ": "+ this.state.currentmsg);
        alert(servedMessages);
       }
    }

    handleChange(e) {
      this.state.currentmsg = e.target.value;
    }

    render(){
     // var msgz = this.state.messages.map( (msg) => <li>{msg}</li>);
      return (
        <div id="chat">
          <Messages messages={servedMessages}/>
          <div id="typehold">
            <input type="text" onChange={this.handleChange} onKeyDown={this.keyPress} placeholder="enter a message..."></input>
          </div>
        </div>
      );
    }
  }
  return <Chat name="haha"/>
}

export default App;
